import React from 'react';
import { Tag, DollarSign, TrendingUp, Clock, CheckCircle } from 'lucide-react';

interface ProductData {
  name: string;
  brand: string;
  price: number;
  confidence: number;
}

interface ProductInfoProps {
  product: ProductData;
}

const ProductInfo: React.FC<ProductInfoProps> = ({ product }) => {
  const getRecommendationColor = (confidence: number) => {
    if (confidence >= 0.8) return 'text-green-600';
    if (confidence >= 0.6) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getRecommendationIcon = (confidence: number) => {
    if (confidence >= 0.8) return <CheckCircle className="w-5 h-5 text-green-600" />;
    if (confidence >= 0.6) return <Clock className="w-5 h-5 text-yellow-600" />;
    return <TrendingUp className="w-5 h-5 text-red-600" />;
  };

  return (
    <div className="space-y-6">
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <h3 className="text-xl font-semibold text-gray-900">{product.name}</h3>
          <div className="flex items-center space-x-2">
            <Tag className="w-4 h-4 text-gray-500" />
            <span className="text-sm text-gray-600">{product.brand}</span>
          </div>
        </div>

        <div className="flex items-center space-x-2">
          <DollarSign className="w-5 h-5 text-green-600" />
          <span className="text-2xl font-bold text-gray-900">
            ${product.price.toFixed(2)}
          </span>
        </div>

        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-600">Detection Confidence:</span>
          <span className={`text-sm font-medium ${getRecommendationColor(product.confidence)}`}>
            {(product.confidence * 100).toFixed(1)}%
          </span>
        </div>
      </div>

      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg p-4 border border-blue-200">
        <div className="flex items-start space-x-3">
          {getRecommendationIcon(product.confidence)}
          <div className="flex-1">
            <h4 className="font-semibold text-gray-900 mb-1">Recommendation</h4>
            <p className="text-sm text-gray-700">
              {product.confidence >= 0.8 
                ? "Great time to buy! This item is at a favorable price point."
                : product.confidence >= 0.6
                ? "Consider waiting. Prices may drop further in the coming weeks."
                : "Prices are currently high. Wait for better deals."
              }
            </p>
          </div>
        </div>
      </div>

      <div className="bg-gray-50 rounded-lg p-4">
        <h4 className="font-semibold text-gray-900 mb-3">Price History</h4>
        <div className="h-32 bg-white rounded border flex items-center justify-center">
          <p className="text-gray-500 text-sm">Price history chart will be displayed here</p>
        </div>
      </div>

      <div className="space-y-3">
        <h4 className="font-semibold text-gray-900">Quick Actions</h4>
        <div className="grid grid-cols-2 gap-3">
          <button className="bg-primary-500 text-white py-2 px-4 rounded-lg hover:bg-primary-600 transition-colors text-sm">
            Set Price Alert
          </button>
          <button className="bg-gray-100 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-200 transition-colors text-sm">
            View Similar Items
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductInfo;
