import React, { useRef, useCallback, useState } from 'react';
import Webcam from 'react-webcam';
import { Camera, Upload, RotateCcw, Sparkles, Zap } from 'lucide-react';

interface CameraCaptureProps {
  onImageCapture: (imageData: string) => void;
  isLoading: boolean;
}

const CameraCapture: React.FC<CameraCaptureProps> = ({ onImageCapture, isLoading }) => {
  const webcamRef = useRef<Webcam>(null);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const [facingMode, setFacingMode] = useState<'user' | 'environment'>('environment');

  const videoConstraints = {
    width: 640,
    height: 480,
    facingMode: facingMode
  };

  const capture = useCallback(() => {
    if (webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        setCapturedImage(imageSrc);
        onImageCapture(imageSrc);
      }
    }
  }, [onImageCapture]);

  const retake = () => {
    setCapturedImage(null);
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setCapturedImage(result);
        onImageCapture(result);
      };
      reader.readAsDataURL(file);
    }
  };

  const toggleCamera = () => {
    setFacingMode(facingMode === 'user' ? 'environment' : 'user');
  };

  return (
    <div className="space-y-6">
      {/* Modern Camera Preview */}
      <div className="relative bg-gradient-to-br from-slate-800 to-slate-900 rounded-2xl overflow-hidden border border-white/10">
        {!capturedImage ? (
          <div className="relative">
            <Webcam
              ref={webcamRef}
              audio={false}
              screenshotFormat="image/jpeg"
              videoConstraints={videoConstraints}
              className="w-full h-80 object-cover"
            />
            {/* Overlay with modern design */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-transparent" />
            <div className="absolute top-4 left-4 bg-black/30 backdrop-blur-sm rounded-xl px-3 py-2">
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                <span className="text-white text-sm font-medium">Live Camera</span>
              </div>
            </div>
          </div>
        ) : (
          <div className="relative">
            <img
              src={capturedImage}
              alt="Captured"
              className="w-full h-80 object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-transparent" />
            <div className="absolute top-4 left-4 bg-green-500/30 backdrop-blur-sm rounded-xl px-3 py-2">
              <div className="flex items-center space-x-2">
                <Sparkles className="w-4 h-4 text-green-400" />
                <span className="text-white text-sm font-medium">Image Captured</span>
              </div>
            </div>
          </div>
        )}

        {/* Modern Camera Controls */}
        {!capturedImage && (
          <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-4">
            <button
              onClick={toggleCamera}
              className="bg-black/40 backdrop-blur-sm rounded-full p-4 hover:bg-black/60 transition-all duration-300 border border-white/20"
              title="Switch Camera"
            >
              <RotateCcw className="w-5 h-5 text-white" />
            </button>
            <button
              onClick={capture}
              disabled={isLoading}
              className="bg-gradient-to-r from-purple-500 to-pink-500 rounded-full p-6 hover:from-purple-600 hover:to-pink-600 disabled:opacity-50 transition-all duration-300 shadow-lg shadow-purple-500/25"
              title="Take Photo"
            >
              <Camera className="w-7 h-7 text-white" />
            </button>
          </div>
        )}
      </div>

      {/* Modern Action Buttons */}
      <div className="flex flex-col sm:flex-row gap-4">
        {!capturedImage ? (
          <>
            <button
              onClick={capture}
              disabled={isLoading}
              className="flex-1 bg-gradient-to-r from-purple-500 to-pink-500 text-white py-4 px-6 rounded-2xl hover:from-purple-600 hover:to-pink-600 disabled:opacity-50 transition-all duration-300 flex items-center justify-center font-medium shadow-lg shadow-purple-500/25"
            >
              <Camera className="w-5 h-5 mr-3" />
              {isLoading ? 'Processing...' : 'Capture Image'}
            </button>
            <label className="flex-1 bg-white/10 backdrop-blur-sm text-white py-4 px-6 rounded-2xl hover:bg-white/20 transition-all duration-300 flex items-center justify-center cursor-pointer font-medium border border-white/20">
              <Upload className="w-5 h-5 mr-3" />
              Upload Image
              <input
                type="file"
                accept="image/*"
                onChange={handleFileUpload}
                className="hidden"
              />
            </label>
          </>
        ) : (
          <button
            onClick={retake}
            className="flex-1 bg-white/10 backdrop-blur-sm text-white py-4 px-6 rounded-2xl hover:bg-white/20 transition-all duration-300 flex items-center justify-center font-medium border border-white/20"
          >
            <RotateCcw className="w-5 h-5 mr-3" />
            Retake Photo
          </button>
        )}
      </div>

      {/* Modern Loading State */}
      {isLoading && (
        <div className="text-center py-6">
          <div className="inline-flex items-center space-x-3 bg-black/30 backdrop-blur-sm rounded-2xl px-6 py-4 border border-white/10">
            <div className="relative">
              <div className="w-8 h-8 border-2 border-purple-500/30 border-t-purple-500 rounded-full animate-spin"></div>
            </div>
            <div className="flex items-center space-x-2">
              <Zap className="w-5 h-5 text-purple-400 animate-pulse" />
              <p className="text-white font-medium">Analyzing your item with AI...</p>
            </div>
          </div>
        </div>
      )}

      {/* Feature Highlights */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mt-6">
        <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
          <div className="flex items-center space-x-3">
            <div className="bg-purple-500/20 p-2 rounded-lg">
              <Sparkles className="w-4 h-4 text-purple-400" />
            </div>
            <div>
              <p className="text-white text-sm font-medium">AI Detection</p>
              <p className="text-gray-400 text-xs">Advanced object recognition</p>
            </div>
          </div>
        </div>
        <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
          <div className="flex items-center space-x-3">
            <div className="bg-green-500/20 p-2 rounded-lg">
              <Zap className="w-4 h-4 text-green-400" />
            </div>
            <div>
              <p className="text-white text-sm font-medium">Price Analysis</p>
              <p className="text-gray-400 text-xs">Real-time market data</p>
            </div>
          </div>
        </div>
